#define STYLE_PAGE \
"\nbody{margin:0;height:100vh;display:flex;flex-direction:column;align-items:center;background-color:#1b325f;font-family:Arial,sans-serif;color:whitesmoke;}" \
"\nheader{text-align:center;margin-top:20px;margin-bottom:none;}" \
"\nheader h1{font-size:2.5rem;color:#ffffff;text-shadow:2px 2px 5px rgba(0,0,0,0.5);}" \
"\n.principal{display:flex;flex-direction:column;align-items:center;width:25rem;padding:20px;background-color:#3a89c9;border-radius:20px;box-shadow:10px 15px 15px rgba(0,0,0,0.35);gap:10px;}" \
"\n#nivel,.estado-led{align-self:flex-start;padding-left:1rem;text-align:left;}" \
"\n.led-ligado{width:30px;height:30px;border-radius:50%;background-color:#00c853;box-shadow:0 0 20px #00c853;}" \
"\n.led-desligado{width:30px;height:30px;border-radius:50%;background-color:#d50000;box-shadow:0 0 20px #f03e3e;}" \
"\n.estado-led{display:flex;justify-content:space-between;align-items:center;gap:12rem;align-self:flex-start;text-align:left;padding:5px 10px;margin-bottom:10px;background-color:#1565c0;border-radius:10px;box-shadow:2px 2px 8px rgba(0,0,0,0.3);width:95%;color:white;}" \
"\n#MinMax{width:95%;background-color:#0d47a1;color:white;border:none;border-radius:10px;padding:10px;text-align:center;box-shadow:2px 2px 8px rgba(0,0,0,0.3);margin-bottom:10px;margin-left:auto;margin-right:auto;box-sizing:border-box;}" \
"\n#camposNumeros{display:flex;width:100%;flex-direction:column;margin-top:10px;padding:10px;border-radius:10px;background-color:#e3f2fd;color:#000;gap:10px;}" \
"\n#camposNumeros label{font-weight:bold;display:flex;flex-direction:column;margin-bottom:5px;}" \
"\n#camposNumeros input{margin-bottom:15px;font-size:1rem;}" \
"\n.Definicao{display:flex;justify-content:center;width:95%;}" \
"\ninput[type=\"number\"]{padding:8px;border:1px solid #999;border-radius:5px;outline:none;font-size:1rem;width:100%;box-sizing:border-box;}" \
"\ninput[type=\"number\"]:focus{border-color:#1976d2;box-shadow:0 0 5px #1976d2;}" \
"\nbutton{padding:10px 20px;cursor:pointer;background-color:#1976d2;color:white;border:none;border-radius:5px;font-size:1rem;transition:background-color 0.3s,transform 0.2s;}" \
"\nbutton:hover{background-color:#0d47a1;transform:scale(1.05);}" \
"\n#nivel{display:flex;justify-content:space-between;align-self:flex-start;text-align:left;padding:5px 10px;margin-bottom:5px;background-color:#1565c0;border-radius:10px;box-shadow:2px 2px 8px rgba(0,0,0,0.3);width:95%;color:white;}" \
"\n#nivel .nivel{font-size:21px;}input[type=\"number\"]::-webkit-outer-spin-button,input[type=\"number\"]::-webkit-inner-spin-button{-webkit-appearance:none;margin:0;}"

//Estilização para o html